import React, { Component } from "react";
import { connect } from "react-redux";
// import * as ACTION_TYPES from "../store/actions/actionTypes";
import * as ACTIONS from "../store/actions/actions";

class Component2 extends Component {
  render() {
    return (
      <div>
        <button onClick={() => console.log(this.state.stateProp1)}>
          Get State
        </button>
        <button onClick={() => this.dispatch.action1()}>
          Dispatch action1
        </button>

        <button onClick={() => this.dispatch.action2()}>
          Dispatch action2
        </button>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    stateProp1: state.stateProp1,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    action1: () => dispatch(ACTIONS.SUCCESS),
    action2: () => dispatch(ACTIONS.FAILURE),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Component2);
